        DB::transaction(function () {
            $this->cb();
            $this->location();
        });
    }

    public function cb()
    {
        $list_cagar = array_filter(Arr::map($this->cagar(), fn ($item) => isset($item['nama_cagar']) ? $item : null));

        foreach ($list_cagar as $key => $item) {
            $cagar = CagarBudaya::firstOrCreate([
                'name' => $item['nama_cagar'],
            ]);
            $cagar->update([
                'cp_name' => $item['nama'],
                'cp_phone' => $item['phone'],
            ]);

            $cagar->address()->updateOrCreate(
                ['name' => $cagar->name],
                [
                    'address' => Arr::get($item, 'alamat', null),
                    'province_code' => Arr::get($item, 'province_code', null),
                    'city_code' => Arr::get($item, 'city_code', null),
                    'district_code' => Arr::get($item, 'district_code', null),
                    'ward_code' => Arr::get($item, 'ward_code', null),
                    'lat' => Arr::get($item, 'lat', null),
                    'lng' => Arr::get($item, 'lng', null),
                ]
            );
        }
    }

    public function location()
    {
        $list_cagar = array_filter(Arr::map($this->latlng(), fn ($item) => isset($item['latitude']) && isset($item['longitude']) ? $item : null));
        foreach ($list_cagar as $key => $item) {
            $name_normal = str_replace("\n", '', str_replace("\r", '', $item['name']));
            $cagar = CagarBudaya::firstOrCreate([
                'name' => $name_normal,
            ]);

            try {
                $cagar->address()->updateOrCreate(
                    ['name' => $cagar->name],
                    [
                        // 'address' => Arr::get($item, 'alamat', null),
                        // 'province_code' => Arr::get($item, 'province_code', null),
                        // 'city_code' => Arr::get($item, 'city_code', null),
                        // 'district_code' => Arr::get($item, 'district_code', null),
                        // 'ward_code' => Arr::get($item, 'ward_code', null),
                        'lat' => floatval(Arr::get($item, 'latitude', 0)),
                        'lng' => floatval(Arr::get($item, 'longitude', 0)),
                    ]
                );
            } catch (\Throwable $th) {
                dd($cagar, $item, $th);
            }
        }
    }

